import React, {Component} from 'react';
import happyMP4 from '../../assets/videos/experience2/mp4/happy.mp4';
import happyWebM from "../../assets/videos/experience2/webm/happy.webm";
// import happyMP3 from "../../assets/audio/happy.mp3";
import featherClick from "../../assets/images/experience2/cursor/feather-click.png";
import featherClicked from "../../assets/images/experience2/cursor/feather-clicked.png";
import {TweenMax} from "gsap/TweenMax";

export default class Happy extends Component {
    setTitle = () => {
        document.title = "Feeling happy today? Let Perfume Talk by MANE";
    };

    constructor(props) {
        super(props);
        this.state = {
            cursor: "cursor__click",
            x: Number,
            y: Number,
            cursorEl: 0
        };
        this.video = React.createRef();
        // this.audio = React.createRef();
        this.test = React.createRef();
        this.setCoordinates = this.setCoordinates.bind(this);
    };

    setCoordinates = e => {
        this.setState({x: e.pageX, y: e.pageY});
    };

    playVideo = () => {
        this.video.current.play();
    };



    clicked = () => {
        this.video.current.play();
        this.video.current.muted = false;
        this.setState({
            cursor: "cursor__click--clicked",
        });
    };

    componentWillUnmount() {

    }

    componentDidMount() {
        // this.listen();
        this.setTitle();
        this.playVideo();
        setTimeout(this.videoStarts, 300);
        document.addEventListener('contextmenu', event => event.preventDefault());

        let rotatingCursor = (function () {
            /* Local Variables */
            const INTERVAL_POSITION = 5;
            const INTERVAL_ROTATION = 100;
            let lastCursorPos = {x: -999, y: -999};
            let currentCursorPos = {x: -999, y: -999};
            let lastCursorAngle = 0, cursorAngle = 0;
            let cursorEl;

            /* Local Functions */

            // NOTE: I am transform two different elements here because so I can only animate the rotation with 'transition-property: transform'.
            function setCurrentCursorProps() {
                // Apply translation (set to actual cursor position)
                cursorEl.style.transform = `translate(${currentCursorPos.x}px, ${currentCursorPos.y}px)`;

                // Ensure correct rotation transition direction
                while (Math.abs(lastCursorAngle - cursorAngle) > 180) {
                    if (cursorAngle > lastCursorAngle) {
                        cursorAngle -= 360;
                    } else if (cursorAngle < lastCursorAngle) {
                        cursorAngle += 360;
                    }
                }
                // Apply rotation
                // cursorImageEl.style.transform = `rotate(${cursorAngle - 90}deg)`;
            }

            function updateCursor() {
                window.addEventListener('mousemove', event => {
                    currentCursorPos = {x: event.clientX, y: event.clientY};
                });

                // Interval for updating cursor-position
                setInterval(setCurrentCursorProps, INTERVAL_POSITION);

                // Interval for updating cursor-rotation
                setInterval(() => {
                    const delt = {
                        x: lastCursorPos.x - currentCursorPos.x,
                        y: lastCursorPos.y - currentCursorPos.y
                    };
                    if (Math.abs(delt.x) < 3 && Math.abs(delt.y) < 3) return;
                    cursorAngle = (Math.atan2(delt.y, delt.x) * 180 / Math.PI);

                    setCurrentCursorProps();

                    lastCursorPos = currentCursorPos;
                    lastCursorAngle = cursorAngle;
                }, INTERVAL_ROTATION);
            }

            /* Public Functions */
            return {
                'initialize': () => {
                    cursorEl = document.querySelector('#cursor');
                    cursorImageEl = document.querySelector('#cursor > img');
                    updateCursor();
                }
            };
        })();
        document.addEventListener('mousemove', rotatingCursor.initialize);
        console.log(rotatingCursor);
    };

    render() {
        return (
            <main onClick={this.clicked} className="letPerfumeTalk"
                  onMouseMove={this.setCoordinates.bind(this)}>
                <video className="letPerfumeTalk__video"
                       autoPlay={true}
                       loop={true}
                       muted={true}
                       ref={this.video}
                >
                    <source src={happyMP4} type='video/mp4; codecs="avc1.4D401E, mp4a.40.2"'/>
                    <source src={happyWebM} type="video/webm"/>
                </video>
                {/*<audio autoPlay*/}
                {/*ref={this.audio}>*/}
                {/*<source src={happyMP3} type="audio/mpeg"/>*/}
                {/*</audio>*/}
                <div id="cursor">
                    <img className={this.state.cursor} alt="feather" src={featherClick}/>
                    <img className="cursor__clicked" alt="feather" src={featherClicked}/>
                </div>
            </main>
        )
    }
}
