import React, {Component} from 'react';
import happyMP4 from '../../assets/videos/experience2/mp4/happy.mp4';
import happyWebM from "../../assets/videos/experience2/webm/happy.webm";
// import happyMP3 from "../../assets/audio/happy.mp3";
// import $ from 'jquery';

export default class Happy extends Component {
    setTitle = () => {
        document.title = "Feeling happy today? Let Perfume Talk by MANE";
    };

    constructor(props) {
        super(props);
        this.state = {
            cursor: 'letPerfumeTalk__video click',
            x: Number,
            y: Number
        };
        this.video = React.createRef();
        // this.audio = React.createRef();
        this.test = React.createRef();
        this.setCoordinates = this.setCoordinates.bind(this);
    };

    setCoordinates = e => {
        this.setState({x: e.pageX, y: e.pageY});
    };

    playVideo = () => {
        this.video.current.play();
    };

    clicked = () => {
        this.video.current.play();
        this.video.current.muted = false;

        this.setState({
            cursor: "letPerfumeTalk__video clicked",
        });

        // this.refs.MyGame.style.opacity = 0;
        // do {
        //     this.refs.MyGame.style.opacity--
        // } while (this.refs.MyGame.style.opacity > 0);
        //
        // if (this.refs.MyGame.style.opacity === 0) {
        //     this.refs.MyGame.remove();
        // }
    };

    componentDidMount() {
        this.setTitle();
        this.playVideo();
        setTimeout(this.videoStarts, 300);
        document.addEventListener('contextmenu', event => event.preventDefault());
        document.addEventListener('mousemove', this.setCoordinates);

    };

    render() {
        return (
            <main onClick={this.clicked} className="letPerfumeTalk"
                  onMouseMove={this.setCoordinates.bind(this)}>
                <video className="letPerfumeTalk__video clicked click"
                       autoPlay={true}
                       loop={true}
                       muted={true}
                       ref={this.video}
                >
                    <source src={happyMP4} type='video/mp4; codecs="avc1.4D401E, mp4a.40.2"'/>
                    <source src={happyWebM} type="video/webm"/>
                </video>
                {/*<audio autoPlay*/}
                {/*ref={this.audio}>*/}
                {/*<source src={happyMP3} type="audio/mpeg"/>*/}
                {/*</audio>*/}
                {/*<div className="MyGame"*/}
                     {/*ref='MyGame'*/}
                     {/*style={{*/}
                         {/*top: this.state.y + 0 + "px",*/}
                         {/*left: this.state.x + 30 + "px",*/}
                     {/*}}>*/}
                    {/*Click, drag<br/>*/}
                    {/*& enjoy!*/}
                {/*</div>*/}
                <div id="cursor"><img alt="Cursor Hand" src="../../assets/images/experience2/cursor/rotating-cursor.svg"/></div>
            </main>
        )
    }
}
