import React, {Component} from 'react';
import happyMP4 from '../../assets/videos/experience2/mp4/happy.mp4';
import happyWebM from "../../assets/videos/experience2/webm/happy.webm";
// import happyMP3 from "../../assets/audio/happy.mp3";
import featherClick from "../../assets/images/experience2/cursor/feather-click.png";
import featherClicked from "../../assets/images/experience2/cursor/feather-clicked.png";
import {rotatingCursor} from "./cursor";
import Image from './Elements'
import {TweenMax} from "gsap/TweenMax";
import $ from 'jquery';

export default class Happy extends Component {
    setTitle = () => {
        document.title = "Feeling happy today? Let Perfume Talk by MANE";
    };

    constructor(props) {
        super(props);
        this.state = {
            cursor: "cursor__click",
            x: Number,
            y: Number,
            element: Symbol
        };
        this.video = React.createRef();
        // this.audio = React.createRef();
        this.test = React.createRef();
        this.setCoordinates = this.setCoordinates.bind(this);
    };

    setCoordinates = e => {
        this.setState({x: e.pageX, y: e.pageY});

        if (e.buttons === 1 || e.which === 1) {
            let logo = React.createElement('img', {
                src: featherClick,
                alt: "experience",
                style: {
                    top: this.state.y + 40 + "px",
                    left: this.state.x + 40 + "px",
                    position: 'absolute',
                    'z-index': '500000000',
                }
            });
            console.log(logo);
        }
    };

    playVideo = () => {
        this.video.current.play();
    };

    clicked = () => {
        this.setCoordinates.bind(this);
        this.video.current.play();
        this.video.current.muted = false;
        this.setState({
            cursor: "cursor__click--clicked",
        });
    };

    componentDidMount() {
        // this.listen();
        this.setTitle();
        this.playVideo();
        setTimeout(this.videoStarts, 300);
        document.addEventListener('contextmenu', event => event.preventDefault());
        rotatingCursor.initialize();
        document.addEventListener('click', this.setCoordinates);
    };

    render() {
        return (
            <main onMouseMove={this.setCoordinates} draggable="false" onClick={this.clicked}
                  className="letPerfumeTalk"
            >
                <video className="letPerfumeTalk__video"
                       autoPlay={true}
                       loop={true}
                       muted={true}
                       ref={this.video}
                >
                    <source src={happyMP4} type='video/mp4; codecs="avc1.4D401E, mp4a.40.2"'/>
                    <source src={happyWebM} type="video/webm"/>
                </video>
                {/*<audio autoPlay*/}
                {/*ref={this.audio}>*/}
                {/*<source src={happyMP3} type="audio/mpeg"/>*/}
                {/*</audio>*/}
                {/*{this.state.}*/}
                <div id="cursor">
                    <img className={this.state.cursor} alt="feather" src={featherClick}/>
                    <img className="cursor__clicked" alt="feather" src={featherClicked}/>
                </div>
            </main>
        )
    }
}
